import React from 'react';
import logo from './logo.svg';
import './App.css';

const PersonForm = (props) =>(
  <div>
      <div>
        <label for='firstname'>
          Firstname:
          <input type='text' />
        </label>
        <button onClick={() => props.clearName ? props.clearName : console.log('Firstname cleared..')}>Clear Firstname</button>
        <button onClick={() => props.savename ? props.saveName(props.firstname) : console.log('Lastname cleared..')}>Save Firstname </button>
      </div>

      <div>
        <label for='lastname'>
          Lastname:
          <input type='text' />
        </label>
        <button>Clear Lastname</button>
        <button>Save Lastname </button>
      </div>
  </div>
)

const App = () => {
  return (
    <div>
      <PersonForm />
    </div>
    );
}

export default App;
